			quotes["Багряна"].Add("А внучката ще носи всичко: името, очите, устните, косите - на незримата.");

			quotes["Славейков"].Add("Слепец! За мен залязоха навеки на слънцето лучите - с звуковете на музиката... А едни, едни те даваха живот на моя дух и светлина на гордите ми чувства. Аз доживях самичък да се видя мъртвец приживе, Другите живеят с живота на творенията мои - и само ази зарад тях съм глух. ");
			quotes["Славейков"].Add("В зори ранил на път, аз дишам на лятно утро свежестта - и милва ми душата бодра за лек път охолна мечта.");
			quotes["Славейков"].Add("За лек път, за почивка тиха през ясна вечер в родний кът, където ме с милувка чака на мойто щастие сънят.");
			quotes["Славейков"].Add("Спи езерото; белостволи буки над него свождат вити гранки, и в тихите му тъмни глъбини преплитат отразени сянки.");
			quotes["Славейков"].Add("Треперят, шептят белостволи буки, а то, замряло, нито трепва... Понякога му сал повърхнини дълга от лист отронен сепва.");
			quotes["Славейков"].Add("Самотен гроб в самотен кът, пустиня около немее — аз зная тоз самотен кът и тоя гроб самотен де е.");
			quotes["Славейков"].Add("И знам, че в тоз самотен гроб, таме в пустинний кът самотен, зарови милвана ръка един немил живот сиротен.");

			quotes["Милев"].Add("Първите паднаха в кърви. Метежният устрем бе посрещнат с куршуми. Знамената изтръпнаха пронизани. Планината гърми. . . Там горе далечни и близки хълми потъмняха обнизани с хора - плъпнаха черни редици редовни платени войници и разлютена милиция.");
			quotes["Милев"].Add("Прекрасно но - що е отечество - И яростно лаят картечници. . . Първите паднаха в кърви. ");
			quotes["Милев"].Add("Войските настъпваха. Под грозния звук на шрапнелите изтръпнаха и най-смелите в отчаяние към небето издигнати голи ръце. Ужас без слава замръзна на всяко лице - очи без страдание.");
			quotes["Милев"].Add("Там посред общия смут сам, като луд епически смелия поп Андрей с легендарния топ стреля снаряд след снаряд. . . В последния миг Смърт на Сатаната! извика побеснял и велик - и обърна назад своя топ последната граната изпрати право там");
			quotes["Милев"].Add("До телеграфния стълб бе изправен. До него палача. Капитана. Въжето бе готово. Балкана тъмнееше мрачен. Небето - сурово. Попа стоеше огромен, изправен в целий си ръст, цял спокоен като гранит - без жал без спомен - на гърдите Христовия кръст и с поглед в балканите впит - далеко сякаш в грядущето.");
			quotes["Милев"].Add("Тогава настана най-ужасното Бясно захласната заудря в душите тревожна камбана - удря, бие, звъни. . . Нощта падна тъй ниско - глухо и страшно заключена от всички страни.");
			quotes["Милев"].Add("Ахил беше грубата сила. Военния демон. Ахил беше стар генерал на Н. Ц. В. цар Агамемнон. Ахил бе герой. С безброй кръстове, ордени, ленти. . . Пиедестал на реда и тишината в страната.");

			Random random = new Random();
			int randomNum = random.Next(0, 10);

			string randomAuthor = authors[randomNum];
			List<string> quotesSequence = quotes[randomAuthor];
			string randomQuote = quotesSequence[random.Next(0, quotesSequence.Count)];

			return Tuple.Create(randomAuthor, randomQuote);
		}
	
