        private void Form1_Load(object sender, EventArgs e)
		{
			var Osem = Matura();
			MaturaAuthor = Osem.Item1;
			MaturaQuote = Osem.Item2;
			label1.Text = MaturaQuote;
			label3.Text = $"Общо отговорени въпроси: {CounterRight + CounterWrong} ,от които {CounterRight} са верни и {CounterWrong} грешни.";


