		private void button1_Click(object sender, EventArgs e)
		{



		if (textBox1.Text.Length < 2)
			{
				label2.Text = "Моля въведете автор";
			}
			else
			{

				if (textBox1.Text.ToLower() == MaturaAuthor.ToLower())
				{
					label2.Text = "Правилен Отговор";
					CounterRight++;
				}
				else
				{
					label2.Text = "Грешен Отговор, правилният автор е:" + MaturaAuthor;
					CounterWrong++;
				}
				double Score = (Convert.ToDouble(CounterRight) / Convert.ToDouble(CounterWrong+CounterRight)) * 4+2;
				textBox1.Text = "";
				label3.Text = $"Общо отговорени въпроси: {CounterRight + CounterWrong} ,от които {CounterRight} са верни и {CounterWrong} грешни.   Оценка: {Math.Round(Score,2)}";
				var Osem = Matura();
				MaturaAuthor = Osem.Item1;
				MaturaQuote = Osem.Item2;
				label1.Text = MaturaQuote;
			}
		}
